  $('#relationship-status-slider').on('change input', function() {
      $('#relationship-status-output').text($(this).val());


    if($('#relationship-status-output').val() <= 100){
      $('#price').text($(this).val()*270)

    }
    else if($('#relationship-status-output').val() <= 250){

      $('#price').text($(this).val()*250)
    }
  });
 